import './App.css';


let x = 0;
let w = 0;
let resizer;
let outputPanel;




function App() {

  return (
    <div id="App">

      {/*Header bar on top of the screen*/}
      <div id="headerBar">

        <div id="logo"></div>

        <div id="dropdown-containers"></div>

      </div>


      {/*Containing the code editor and the output panel*/}
      <div id="workspace">

        <div id="editor"></div>

        <div id="outputPanel">
          <div id="resizer"></div>
          <div id="output">Output:</div>
        </div>

      </div>

    </div>
  );
}





const dragHandler = function(e) {
  // get mouse x position
  x = e.clientX;

  // calculate width of the outputPanel
  let style = window.getComputedStyle(outputPanel);
  w = parseInt(style.width, 10);

  // attach the listenser to document
  document.addEventListener('mousemove', mouseMoveHandler);
  document.addEventListener('mouseup', mouseUpHandler);
}






const mouseMoveHandler = function(e) {
  // distance the mouse has moved
  let dx = e.clientX - x;
  // adjust the dimensions of the element
  outputPanel.style.width = `${w - dx}px`;
}






const mouseUpHandler = function() {
  // remove the handler of 'mousemove and 'mouseup'
  document.removeEventListener('mousemove', mouseMoveHandler);
  document.removeEventListener('mouseup', mouseUpHandler);
}




export default App;
