import React from 'react';
import './App.css';
import Dropdown from './components/Dropdown';
import ResizableBox from './components/OutputPanel';
import { themeOptions, languageOptions } from './components/options';

import AceEditor from 'react-ace';
import 'ace-builds/src-min-noconflict/mode-python';
import 'ace-builds/src-min-noconflict/theme-dracula';




class App extends React.Component {


  constructor() {
    super();
  }




  onLanguageChanged(language) {

  }



  onThemeChanged(theme) {

  }



  createEditor() {
    return (
      <AceEditor  id="editor" mode='python' theme='dracula' onChange={this.onEdit} 
                  enableBasicAutocompletion={true} enableLiveAutocompletion={true}
      ></AceEditor>
    );
  }




  render() {

    return (
      <div id="App">

        {/*Header bar on top of the screen*/}
        <div id="headerBar">

          <div id="logo">
            <image></image>
            <label>Online IDE</label>
          </div>

          <div id="buttonsContainer">

            <div id="dropdownContainer">
              <Dropdown id="themeDropdown" width="150px" label="Theme" 
                  options={themeOptions} onSelect={this.onThemeChanged} />
              <Dropdown id="langDropdown" width="150px" label="Language"
                options={languageOptions} onSelect={this.onLanguageChanged} />
            </div>

            <button id="runButton">Run Code</button>

          </div>

        </div>


        {/*Containing the code editor and the output panel*/}
        <div id="workspace">

          <div id="editor">



          </div>

          <ResizableBox id="outputPanel">
            <div id="output">Output:</div>
          </ResizableBox>

        </div>

      </div>
    );
  }
}




export default App;
