import React from 'react'
import './Dropdown.css';



class Dropdown extends React.Component {

    constructor(props) {
        super();
        this.val = null;
        this.props = props;
        this.dropdown = React.createRef();
        this.onChangeEvent = this.onChangeEvent.bind(this);
    }


    onChangeEvent() {
        this.val = this.dropdown.options[this.dropdown.selectedIndex].value;
        alert(this.value);
        this.props.onSelect();
    }


    get value() {
        return this.val;
    }


    render() {
        return (
            <div id="container">

                <label id="label"> {this.props.label} </label>

                <select id="dropdown" onChange={this.onChangeEvent} 
                        style={{width:this.props.width}} ref={this.dropdown} >



                </select>

            </div>
        );
    }
}


export default Dropdown;
