import React from 'react'
import './OutputPanel.css'



class ResizableBox extends React.Component {

    constructor() {
        super()
        this.x = 0
        this.w = 0
    }



    dragHandler(e) {
        // get mouse x position
        this.x = e.clientX;

        // calculate width of the outputPanel
        let style = window.getComputedStyle(this.outputPanel);
        this.w = parseInt(style.width, 10);

        // attach the listenser to document
        document.addEventListener('mousemove', this.mouseMoveHandler);
        document.addEventListener('mouseup', this.mouseUpHandler);
    }



    mouseMoveHandler(e) {
        // distance the mouse has moved
        let dx = e.clientX - this.x;
        // adjust the dimensions of the element
        this.style.width = `${this.w - dx}px`;
    }



    mouseUpHandler() {
        // remove the handler of 'mousemove and 'mouseup'
        document.removeEventListener('mousemove', this.mouseMoveHandler);
        document.removeEventListener('mouseup', this.mouseUpHandler);
    }



    render() {
        return (
            <div id="outputPanel">
                <div id="resizer"></div>
                <div id="output"></div>
            </div>
        );
    }
}


export default ResizableBox;
