import React from 'react'
import './Dropdown.css';



export const Dropdown = (props) => {
    return (
        <div id="container">

            <label id="label"> {props.label} </label>

            <select id="dropdown" style={{width:props.width}}>
            {props.options.map(({ value, label }, index) => <option value={value} >{label}</option>)}
            </select>

        </div>
    )
}


export default Dropdown;
